function populateTable(fixPack) {
	var div = document.getElementById("release-notes");

	div.innerHTML = '';

	for (var component in fixPack) {
		var table  = document.createElement('table');

		var link = document.createElement('a');

		link.setAttribute('id',  component);

		link.innerHTML = component;

		table.insertRow().insertCell().appendChild(link);

		fillTable(table, fixPack[component]);

		div.appendChild(table);
	}

	populateComponentLinks(fixPack);
}

function fillTable(table, tickets) {
	window.tickets = tickets;
	for (var i = 0; i < tickets.length; i++) {
		for (var ticket in tickets[i]) {
			var tr = table.insertRow();
			var rowDiv = document.createElement('div');
			var link = document.createElement('a');

			link.setAttribute('href', 'https://issues.liferay.com/browse/' + ticket);
			link.innerHTML = ticket;

			rowDiv.appendChild(link);
			rowDiv.appendChild(document.createElement('br'));
			rowDiv.appendChild(document.createTextNode(tickets[i][ticket]["summary"]));

			tr.insertCell().appendChild(rowDiv);
		}
	}
}

function populateComponentLinks(fixPack) {
	var div = document.getElementById("component-links");

	div.innerHTML = '<ul>';

	for (var component in fixPack) {
		var list = document.createElement('li');
		var link = document.createElement('a');

		link.setAttribute('href', 'index.html#' + component);
		link.innerHTML = component;

		list.appendChild(link);

		div.appendChild(list);
	}

	div.innerHTML += "</ul>";
}

function populateNav() {
	var div = document.getElementById("fix-packs");

	var select = document.createElement('select');

	select.setAttribute("id", "fix-pack-select");
	select.setAttribute("onchange", "selectFixPack()");

	div.appendChild(select);

	var option = document.createElement('option');

	option.setAttribute("disabled", "");
	option.setAttribute("selected", "");
	option.innerHTML = "--Select--";

	select.appendChild(option);

	for (var fixPack in data) {
		var option = document.createElement('option');

		option.setAttribute("data", fixPack);
		option.setAttribute("value", fixPack);
		option.innerHTML = "liferay-fixpack-de-" + fixPack + "-7010";

		select.appendChild(option);
	}
}

function selectFixPack() {
	var select = document.getElementById('fix-pack-select');

	var selectValue = select.value;

	if (selectValue == 4) {
		selectValue = 3;
	}

	populateTable(data[selectValue]);
}